import React from 'react';
import * as d3 from 'd3';


class Pr extends React.Component {
	constructor(props){
		super(props);
		this.myRef = React.createRef();
		this.dataset = [100, 200, 300, 400, 500];
	}
	componentDidMount(){
		let size = 500;
		let svg = d3.select(this.myRef.current)
					.append('svg')
					.attr('width', size)
					.attr('height', size);
	}
	render(){
		return (
			<div ref={this.myRef}>
			</div>
		);
	}

}

export default Pr;
