import React from 'react';
// import React, { useContext } from 'react';
import { Card, Form, Input, Row, Col, Button, Slider } from 'antd';
// import { Card, Form, Input, Row, Col, Button, Select, Slider } from 'antd';
// import { QosOption } from './index'

const Publisher = ({ publish }) => {
	const [form] = Form.useForm();
	// const qosOptions = useContext(QosOption);

	const removeElementsByClass = (className) => {
		const elements = document.getElementsByClassName(className);
		while(elements.length > 0){
			elements[0].parentNode.removeChild(elements[0]);
		}
	}

	const modelLoad = (value) => {
		// New panels are created with class 'panel_div', we need to remove this elements before create new ones
		removeElementsByClass('jsPanel')

		// In the Lua side, models must be created in a folder named as the model file and this folder must be placed inside the Resources folder
		const fileName = value.target.files[0].name
		const dirName = fileName.split('.')[0]

		console.log('Load:' + dirName + '/' + fileName)

		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'Load|' + dirName + '/' + fileName
		})
	};

	const modelSetup = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'Setup'
		})
	};

	const modelStep = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'Step'
		})
	};

	const modelRun = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'Run'
		})
	};

	const modelSpeed = (value) => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'Speed:'+value
		})
	};

	const modelViewStats = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'ToggleView:Stats'
		})
	};

	const modelViewWindows = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'ToggleView:Windows'
		})
	};

	const modelViewFamilies = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'ToggleView:Families'
		})
	};

	const modelViewGrid = () => {
		publish({
			topic: 'from_client',
			qos: 0,
			payload: 'ToggleView:Grid'
		})
	};

	const PublishForm = (
	<Form
		layout="vertical"
		name="basic"
		form={form}
	>
		<Row gutter={20}>
			<Col span={8} >
				<Input type='file' onChange={modelLoad}/>
			</Col>
			<Col span={8} >
				{/* <Button onClick={modelLoad}>	Load	</Button> */}
				<Button onClick={modelSetup}>	Setup	</Button>
				<Button onClick={modelStep}>	Step	</Button>
				<Button onClick={modelRun}>		Run		</Button>
				<br/>
				<br/>
				<label>Speed:</label>
				<Slider min={1} max={10} defaultValue={10} onChange={modelSpeed} />
			</Col>
			<Col span={8} >
				<Button onClick={modelViewStats}> 	view Stats </Button>
				<Button onClick={modelViewWindows}> view Windows </Button>
				<Button onClick={modelViewFamilies}>view Families </Button>
				<Button onClick={modelViewGrid}> 	view Grid </Button>
			</Col>
		</Row>
	</Form>
	)

	return (
	<Card
		title="Control Panel"
	>
		{PublishForm}
	</Card>
	);
}

export default Publisher;
