import React, { createContext, useEffect, useReducer, useState } from 'react';
import Connection from './Connection';
import Publisher from './Publisher';
import Subscriber from './Subscriber';
import Receiver from './Receiver';
import mqtt from 'mqtt';

// export const QosOption = createContext([])
// const qosOption = [
//   {
//     label: '0',
//     value: 0,
//   }, {
//     label: '1',
//     value: 1,
//   }, {
//     label: '2',
//     value: 2,
//   },
// ];


export const NewContext = createContext([])
const initialState = {
	setupClicked: false,
}

function changeState(state, action) {
	switch(action.type){
		case 'setup':
			return {
				setupClicked: true
			};
		default:
			return initialState
	}
}

const [state, dispatch] = useReducer(changeState, initialState);



const HookMqtt = () => {
	const [client, setClient] = useState(null);
	const [isSubed, setIsSub] = useState(false);
	const [payload, setPayload] = useState({});
	const [connectStatus, setConnectStatus] = useState('Connect');

	const mqttConnect = (host, mqttOption) => {
		setConnectStatus('Connecting');
		setClient(mqtt.connect(host, mqttOption));
	};

	useEffect(() => {
		if (client) {
			client.on('connect', () => {
				setConnectStatus('Connected');
			});
			client.on('error', (err) => {
				console.error('Connection error: ', err);
				client.end();
			});
			client.on('reconnect', () => {
				setConnectStatus('Reconnecting');
			});
			client.on('message', (topic, message) => {
				const payload = { topic, message: message.toString() };
				setPayload(payload);
			});
		}
	}, [client]);

	const mqttDisconnect = () => {
		if (client) {
			client.end(() => {
				setConnectStatus('Connect');
			});
		}
	}

	const mqttPublish = (context) => {
		if (client) {
			const { topic, qos, payload } = context;
			client.publish(topic, payload, { qos }, error => {
				if (error) {
					console.log('Publish error: ', error);
				}
			});
		}
	}

	const mqttSub = (subscription) => {
		if (client) {
			const { topic, qos } = subscription;
			client.subscribe(topic, { qos }, (error) => {
				if (error) {
					console.log('Subscribe to topics error', error)
					return
				}
				setIsSub(true)
			});
		}
	};

	const mqttUnSub = (subscription) => {
		if (client) {
			const { topic } = subscription;
			client.unsubscribe(topic, error => {
				if (error) {
					console.log('Unsubscribe error', error)
					return
				}
				setIsSub(false);
			});
		}
	};

	return (
		<>
			<Connection connect={mqttConnect} disconnect={mqttDisconnect} connectBtn={connectStatus} />
			<NewContext.Provider value={{state, dispatch}}>
				<Subscriber sub={mqttSub} unSub={mqttUnSub} showUnsub={isSubed} />
				<Publisher publish={mqttPublish} />
			</NewContext.Provider>
			<Receiver payload={payload} publish={mqttPublish} />
		</>
	);
}

export default HookMqtt;
