import React, { useEffect, useState, useContext } from 'react';
import { Card, List } from 'antd';
import createWindow from '../CreatePanel/CreatePanel'
import Chart from '../D3/Chart/Chart'
import { NewContext } from './index'

import './hook.css'

const Receiver = ({ payload, publish }) => {
	const {state, dispatch} 	  = useContext(NewContext);
	const [init, setInit]         = useState(false);
	const [messages, setMessages] = useState([]);

	// "monitors" is an Array of Maps. Each one of this map contains the information to build a graphical monitor.
	// This maps have two keys -> "id" is the name of the var we want to track and "values" are a list of maps, each one of them containing two keys -> time (x axis) and a value (y axis) associated to this time. 
	// When component is created, "monitors" is an empty Array, but it will be populated with the mqtt messagges.

	// const monitors = [
	// 	{ id: 'var_1', values: [ {time: 0, value: 11}, {time: 1, value: 20}, ] },
	// 	{ id: 'var_2', values: [ {time: 0, value: 11}, {time: 1, value: 20}, ] }
	// ];
	const [monitors, setMonitors] = useState([]);
	const [monitors2, setMonitors2] = useState({});

	useEffect( () => {
		if (state.setupClicked) {
			setMonitors([]);
			dispatch({type: 'unSetup'});
		}
	}, [state]);

	useEffect(() => {
		if (payload.topic) {

			const jsonObject = JSON.parse(payload.message);
			setMessages(messages => [...messages, payload]);

			if (payload.topic.includes('panel_info') ) {
				for (const [k,v] of Object.entries(jsonObject) ) {
					if (k !== 'Monitor') {
						createWindow(v, publish)
					}
				}
			}
			else if ( payload.topic.includes('update') ) {
				if (Object.keys(monitors2).length === 0) {
				// if (monitors.length === 0) {
					setInit(true)

					for (const [k,v] of Object.entries(jsonObject) ) {
						// const vals = {
						// 	id: k,
						// 	values: [
						// 		{time: 0, value:v},
						// 	]
						// };
						// monitors.push(vals)
						monitors2[k] = [{time: 0, value:v}];
					}

				}else{
					for (const [k,v] of Object.entries(jsonObject) ) {
						// for (let aVar of monitors) {
						// 	if (aVar.id === k) {
						// 		const lastElement  	= aVar.values[aVar.values.length -1];
						// 		const newTime  		= lastElement.time + 1;
						// 		const newInput		= {time: newTime, value:v};
						// 		aVar.values.push(newInput)
						// 	}
						// }

						const targetVar 	= monitors2[k];

						const lastElement  	= targetVar[targetVar.length -1];
						const newTime  		= lastElement.time + 1;
						const newInput		= {time: newTime, value:v};
						targetVar.push(newInput)

					}
				}
			}
		}
	}, [payload]);

	const renderListItem = (item) => (
		<List.Item>
			<List.Item.Meta
				title={item.topic}
				description={item.message}
			/>
		</List.Item>
	);

	return (
		<Card title="Receiver">
			{/* When Uncommented, incoming MQTT messages are displayed */}
			{/* <List
				size="small"
				bordered
				dataSource={messages}
				// renderItem={renderListItem}
				style={{maxHeight:'15em' ,overflow:'scroll'}}
			/> */}
			{ init && 
				<div id='allMonitorsDiv'>
					{Object.keys(monitors2).map(function(key) {
						const newMonitor = [{id:key, values:monitors2[key]}]
						return <div key={key} className='monitorDiv'> <Chart data={newMonitor} /> </div>;
				  	})}
				</div>
			}
			{/* { init && 
				<div id='allMonitorsDiv'>
					{monitors.map(function(item) {
						const newMonitor = [item]
						return <div key={item.id} class='monitorDiv'> <Chart data={newMonitor} /> </div>;
				  	})}
				</div>
			} */}

		</Card>
	);
}

export default Receiver;
