import React from 'react';
import { scaleOrdinal } from 'd3-scale';
import { arc as d3Arc, pie as d3Pie } from 'd3-shape';
// import { csvParse } from 'd3-dsv';

// // Same as data.csv
// import dataCsv from './data.csv';


// import './chart.css';

const dataCsv = [
	{age: 5 	,population: 2704659},
	{age: 6 	,population: 3704659},
	{age: 7 	,population: 4704659},
	{age: 8 	,population: 5704659},
	{age: 9 	,population: 2704659},
	{age: 10 	,population: 3704659},
	{age: 11 	,population: 4704659},
	{age: 12 	,population: 5704659},


]

const width = 960,
  height = 500,
  radius = Math.min(width, height) / 2;

const color = scaleOrdinal().range([
  '#98abc5',
  '#8a89a6',
  '#7b6888',
  '#6b486b',
  '#a05d56',
  '#d0743c',
  '#ff8c00',
]);

const arc = d3Arc()
  .outerRadius(radius - 10)
  .innerRadius(radius - 70);

const pie = d3Pie()
  .sort(null)
  .value(function(d) {
    return d.population;
  });

const data = pie(
  csvParse(dataCsv, d => {
    d.population = +d.population;
    return d;
  })
);

// <DonutChart/>
export default () => {
  return (
    <svg width={width} height={height}>
      <g transform={`translate(${width / 2}, ${height / 2})`}>
        {data.map(d => (
          <g className="arc" key={`a${d.data.age}`}>
            <path d={arc(d)} fill={color(d.data.age)} />
            <text transform={`translate(${arc.centroid(d)})`} dy=".35em">
              {d.data.age}
            </text>
          </g>
        ))}
      </g>
    </svg>
  );
};
