import React, { useEffect, useState } from 'react';
import { Card, List } from 'antd';
import createWindow from '../CreatePanel/CreatePanel'
import Chart from '../D3/Chart/Chart'

const Receiver = ({ payload, publish }) => {
	const [messages, setMessages] = useState([])
	const [monitors, setMonitors] = useState([])
	const [init, setInit]         = useState(false)


	// Datos de prueba. Tenemos una lista de variables a representar.
	// Cada entrada de esta lista es un map con 2 pares: 
	// id:nombre_variable, values:Lista_de_valores (que a su vez es un map con 2 pares -> time (valor en eje x) y valor de ese instante (valor en eje y)  )
	const values_1 = {
		id: 'a_var_to_track',
		values: [
			{time: 0, value:1},
		]
	};

	const values_2 = {
		id: 'acum_values',
		values: [
			{time: 0, value: 1},
		]
	};

	const initialData = [
		values_1,
		values_2
	];

	useEffect(() => {
		if (payload.topic) {
			setMessages(messages => [...messages, payload]);
			if (payload.topic.includes('panel_info') ) {

				console.log('--- start panel_info ---');
				const jsonObject = JSON.parse(payload.message);

				for (const [k,v] of Object.entries(jsonObject) ) {
					if (k === 'Monitor') {console.log('Creando ventana: ', k)}
					createWindow(v, publish)
				}

				console.log('--- end ---');
			}
			else if ( payload.topic.includes('update') ) {

				console.log('--- start update ---');
				const jsonObject = JSON.parse(payload.message);

				if (monitors.length === 0) {
					setInit(true)

					for (const [k,v] of Object.entries(jsonObject) ) {
						console.log('\nk',k,'\nv',v)
						const vals = {
							id: k,
							values: [
								{time: 0, value:v},
							]
						};
					}

				}else{

				}
				for (const [k,v] of Object.entries(jsonObject) ) {
					console.log('\nk',k,'\nv',v)
				}

				console.log('--- end ---');
			}
		}
	}, [payload, publish]);

	const renderListItem = (item) => (
		<List.Item>
			<List.Item.Meta
				title={item.topic}
				description={item.message}
			/>
		</List.Item>
	);


	return (
		<Card title="Receiver">
			<List
				size="small"
				bordered
				dataSource={messages}
				renderItem={renderListItem}
				style={{maxHeight:'15em' ,overflow:'scroll'}}
			/>
			{ init && <Chart data={initialData} /> }

		</Card>
	);
}

export default Receiver;
