import React, { useState, useEffect } from 'react';
import { scaleOrdinal, scaleLinear } from 'd3-scale';
import { schemeCategory10 } from 'd3-scale-chromatic';
import { line as d3Line, curveBasis } from 'd3-shape';
import { min, max } from 'd3-array';
import { axisBottom, axisLeft } from 'd3-axis';
import { select } from 'd3-selection';

import './cssChart.css';


const Chart = (props) => {

	const svgWidth 	= 960;
	const svgHeight = 500;

	const margin 	= { top: 20, right: 80, bottom: 30, left: 50 };
	const width 	= svgWidth - margin.left - margin.right;
	const height 	= svgHeight - margin.top - margin.bottom;

	const x 		= scaleLinear().range([0, width]);
	const y 		= scaleLinear().range([height, 0]);
	const z 		= scaleOrdinal(schemeCategory10);

	const line = d3Line()
		.curve(curveBasis)
		.x(d => x(d.time))
		.y(d => y(d.value));

	const values_1 = {
		id: 'a_var_to_track',
		values: [
			{time: 1, value:5},
		]
	}

	const values_2 = {
		id: 'acum_values',
		values: [
			{time: 1, value: 50 + Math.random()*50},
		]
	}

	const initialData = [
		values_1,
		values_2
	]

	const [data, updateData] = useState(initialData)


	x.domain([
		min(data, c => min(c.values, d => d.time)),
		max(data, c => max(c.values, d => d.time)),
	]);
	y.domain([
		min(data, c => min(c.values, d => d.value)),
		max(data, c => max(c.values, d => d.value)),
	]);
	z.domain(data.map(c => c.id));

	console.log(x.domain()[0])

	const [minX1, setMinX1 ] = useState(x.domain()[0])
	const [maxX1, setMaxX1 ] = useState(x.domain()[1])

	const newPoint = () => {
		const newX = maxX1 + 1

		const newItem1 = {time: newX, value: 50 + Math.random()*50}
		const newItem2 = {time: newX, value: 50 + Math.random()*50}

		data[0].values.push(newItem1)
		data[1].values.push(newItem2)

		console.log('new element',data)

		setMaxX1(newX)
		updateData(data)
	}

	return(
		<>
			<svg width={svgWidth} height={svgHeight}>
				<g transform={`translate(${margin.left}, ${margin.top})`}>
					<g
						className="axis axis--x"
						transform={`translate(0, ${height})`}
						ref={node => select(node).call(axisBottom(x))}
					/>
					<g className="axis axis--y" ref={node => select(node).call(axisLeft(y))}>
						<text transform="rotate(-90)" y="6" dy="0.71em" fill="#000">
							value
						</text>
					</g>
					{data.map(trackedVar => {
						const [lastD] = trackedVar.values.slice(-1);
						return (
							<g className="trackedVar" key={trackedVar.id}>
								<path
									className="line"
									d={line(trackedVar.values)}
									style={{ stroke: z(trackedVar.id) }}
								/>
								<text
									transform={`translate(${x(lastD.time)}, ${y(lastD.value)})`}
									x={3}
									dy="0.35em"
									style={{ font: '10px sans-serif' }}
								>
									{trackedVar.id}
								</text>
							</g>
						);
					})}
				</g>
			</svg>

			<button onClick={newPoint} >New Point</button>
		</>
	);
};

export default Chart
